(()=>{"use strict";let t=[];const e=()=>{const t=JSON.parse(localStorage.getItem("tasksArray"));for(let e=0;e<t.length;e++){let i=document.getElementById("taskView-container");const n=`<div class="task">\n                                <div id="task-title">${t[e].title}</div>\n                                <div id="task-description">${t[e].description}</div>\n                                <div id="task-date">${t[e].date}</div>\n                                <div id="task-priority">${t[e].priority}</div>\n                            </div>`;i.innerHTML+=n}};document.addEventListener("DOMContentLoaded",(()=>{document.getElementById("main-container").innerHTML+='<div id="taskForm">\n                <input type="sumbit" id="title">\n                <input type="text" id="description">\n                <input type="date" id="date">\n                <select id="priority">\n                    <option value="High">High</option>\n                    <option value="Middle">Middle</option>\n                    <option value="Low">Low</option>\n                </select>\n                <input type="submit" id="submitTask">\n                </div>',document.getElementById("taskForm").style.visibility="hidden",e(),document.querySelector("#createTask").addEventListener("click",(()=>{(()=>{let t=document.getElementById("taskForm");"hidden"==t.style.visibility?t.style.visibility="visible":t.style.visibility="hidden"})()})),document.querySelector("#submitTask").addEventListener("click",(()=>{((e,i,n,d)=>{let o={title:document.getElementById("title").value,description:document.getElementById("description").value,date:document.getElementById("date").value,priority:document.getElementById("priority").value};t.push(o),localStorage.setItem("tasksArray",JSON.stringify(t))})(),e()}))}))})();